;102&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;103&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;104&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;105&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;#Áü≥Â§¥Ââ™ÂàÄÂ∏É&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;pip install opencv-python&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;pip install opencv-contrib-python # ÂåÖÂê´Êõ¥Â§öÈ´òÁ∫ßÂäüËÉΩ&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;pip install opencv-python mediapipe&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;import cv2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;import mediapipe as mp&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;import numpy as np&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;# ÂàùÂßãÂåñMediaPipeÊâãÈÉ®Ê®°Âûã&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp_hands = mp.solutions.hands&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;hands = mp_hands.Hands(&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    static_image_mode=False,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    max_num_hands=1,  # Âè™Ê£ÄÊµã‰∏ÄÂè™ÊâãÔºàÁÆÄÂåñÈÄªËæëÔºâ&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    min_detection_confidence=0.7,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    min_tracking_confidence=0.5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mp_drawing = mp.solutions.drawing_utils&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;# ÊâìÂºÄÊëÑÂÉèÂ§¥&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;cap = cv2.VideoCapture(0)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;# ÂÆö‰πâÊâãÂäøÂà§Êñ≠ÂáΩÊï∞&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;def get_gesture(hand_landmarks, img_shape):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    h, w = img_shape[:2]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    tip_ids = [4, 8, 12, 16, 20]  # ÊåáÂ∞ñÂÖ≥ÈîÆÁÇπÁ¥¢ÂºïÔºàÊãáÊåáÂà∞Â∞èÊåáÔºâ&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    fingers = []&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    # --- Âà§Êñ≠ÊãáÊåáÊòØÂê¶‰º∏Áõ¥ÔºàÈúÄÁâπÊÆäÂ§ÑÁêÜÔºâ---&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    thumb_tip = hand_landmarks.landmark[tip_ids[0]]  # ÊãáÊåáÊåáÂ∞ñ&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    thumb_mcp = hand_landmarks.landmark[2]  # ÊãáÊåáÊ†πÈÉ®ÂÖ≥ËäÇ&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    # Ê®™ÂêëÊâãÂäøÔºöÊãáÊåáÊåáÂ∞ñxÂùêÊ†áË∂ÖËøáÊ†πÈÉ®ÂÖ≥ËäÇÂàô‰∏∫‰º∏Áõ¥&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    if thumb_tip.x &amp;gt; thumb_mcp.x:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        fingers.append(1)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    else:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        fingers.append(0)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    # --- Âà§Êñ≠ÂÖ∂‰ªñÂõõÊåáÊòØÂê¶‰º∏Áõ¥ ---&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for id in range(1, 5):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        tip = hand_landmarks.landmark[tip_ids[id]]  # ÊåáÂ∞ñ&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        dip = hand_landmarks.landmark[tip_ids[id] - 1]  # Á¨¨‰∫åÊåáËäÇ&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        # ÊåáÂ∞ñÁöÑyÂùêÊ†á &amp;lt; Á¨¨‰∫åÊåáËäÇÁöÑyÂùêÊ†á ‚Üí ÊâãÊåá‰º∏Áõ¥&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if tip.y &amp;lt; dip.y:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            fingers.append(1)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        else:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            fingers.append(0)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    # --- Ê†πÊçÆ‰º∏Áõ¥ÊâãÊåáÊï∞ÈáèÂà§Êñ≠ÊâãÂäø ---&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    total_fingers = sum(fingers)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    # ÊâãÂäøÈÄªËæëËßÑÂàô&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    if total_fingers == 0:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        return &amp;quot;Rock&amp;quot;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    elif total_fingers == 5:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        return &amp;quot;Paper&amp;quot;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    elif fingers[1] == 1 and fingers[2] == 1 and total_fingers == 2:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        return &amp;quot;Scissors&amp;quot;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    else:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        return &amp;quot;Unknown&amp;quot;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;# ‰∏ªÂæ™ÁéØ&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;while cap.isOpened():&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    ret, frame = cap.read()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    if not ret:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        break&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    # Â§ÑÁêÜÂõæÂÉè&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    rgb_frame = cv2.cvtColor(frame, cv2.COLOR_BGR2RGB)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    results = hands.process(rgb_frame)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    if results.multi_hand_landmarks:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        for hand_landmarks in results.multi_hand_landmarks:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            # ÁªòÂà∂ÂÖ≥ÈîÆÁÇπ&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            mp_drawing.draw_landmarks(&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                frame, hand_landmarks, mp_hands.HAND_CONNECTIONS,&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                mp_drawing.DrawingSpec(color=(0, 255, 0), thickness=2),&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                mp_drawing.DrawingSpec(color=(0, 0, 255), thickness=2)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            )&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            # ËÆ°ÁÆóËæπÁïåÊ°Ü&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            x_coords = [int(lm.x * frame.shape[1]) for lm in hand_landmarks.landmark]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            y_coords = [int(lm.y * frame.shape[0]) for lm in hand_landmarks.landmark]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            x_min, x_max = max(0, min(x_coords)), min(frame.shape[1], max(x_coords))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            y_min, y_max = max(0, min(y_coords)), min(frame.shape[0], max(y_coords))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            # ËØÜÂà´ÊâãÂäø&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            gesture = get_gesture(hand_landmarks, frame.shape)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            # ÁªòÂà∂ÁªìÊûú&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            cv2.rectangle(frame, (x_min, y_min), (x_max, y_max), (0, 255, 0), 2)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            cv2.putText(frame, gesture, (x_min, y_min - 10),&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        cv2.FONT_HERSHEY_SIMPLEX, 1, (0, 255, 0), 2)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    # ÊòæÁ§∫ÁîªÈù¢&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    cv2.imshow(&amp;#x27;Rock Paper Scissors&amp;#x27;, frame)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    if cv2.waitKey(1) == 27:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        break&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;cap.release()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;cv2.destroyAllWindows()&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;/li&gt;
