s))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            # ËØÜÂà´ÊâãÂäø&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            gesture = get_gesture(hand_landmarks, frame.shape)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            # ÁªòÂà∂ÁªìÊûú&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            cv2.rectangle(frame, (x_min, y_min), (x_max, y_max), (0, 255, 0), 2)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            cv2.putText(frame, gesture, (x_min, y_min - 10),&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        cv2.FONT_HERSHEY_SIMPLEX, 1, (0, 255, 0), 2)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    # ÊòæÁ§∫ÁîªÈù¢&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    cv2.imshow(&amp;#x27;Rock Paper Scissors&amp;#x27;, frame)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    if cv2.waitKey(1) == 27:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        break&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;cap.release()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;cv2.destroyAllWindows()&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
